package com.lhl.common.action;import java.io.IOException;import java.io.PrintWriter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import org.apache.struts2.ServletActionContext;import com.opensymphony.xwork2.ActionSupport;public class BaseAction extends ActionSupport {	private static final long serialVersionUID = 1L;	protected HttpServletRequest request;	protected HttpServletResponse response;	protected HttpSession session;	protected PrintWriter out;	public HttpServletRequest getRequest() {		this.request = ServletActionContext.getRequest();		return request;	}	public HttpSession getSession() {		this.session = ServletActionContext.getRequest().getSession();		return session;	}	public HttpServletResponse getResponse() {		this.response = ServletActionContext.getResponse();		return response;	}	public PrintWriter getOut() {		HttpServletResponse response = ServletActionContext.getResponse();		response.setContentType("textml;charset=utf-8");		try {			this.out = response.getWriter();		}		catch (IOException e) {			throw new RuntimeException();		}		return out;	}}